	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(ClickPeer::DATABASE_NAME);

		if ($this->isColumnModified(ClickPeer::ID)) $criteria->add(ClickPeer::ID, $this->id);
		if ($this->isColumnModified(ClickPeer::TIME)) $criteria->add(ClickPeer::TIME, $this->time);
		if ($this->isColumnModified(ClickPeer::PROCESSED)) $criteria->add(ClickPeer::PROCESSED, $this->processed);
		if ($this->isColumnModified(ClickPeer::USER_ID)) $criteria->add(ClickPeer::USER_ID, $this->user_id);
		if ($this->isColumnModified(ClickPeer::ADVERT_ID)) $criteria->add(ClickPeer::ADVERT_ID, $this->advert_id);
		if ($this->isColumnModified(ClickPeer::CLICKER_EMAIL)) $criteria->add(ClickPeer::CLICKER_EMAIL, $this->clicker_email);
		if ($this->isColumnModified(ClickPeer::UA_ID)) $criteria->add(ClickPeer::UA_ID, $this->ua_id);
		if ($this->isColumnModified(ClickPeer::AD_ID)) $criteria->add(ClickPeer::AD_ID, $this->ad_id);
		if ($this->isColumnModified(ClickPeer::AID)) $criteria->add(ClickPeer::AID, $this->aid);
		if ($this->isColumnModified(ClickPeer::IP)) $criteria->add(ClickPeer::IP, $this->ip);
		if ($this->isColumnModified(ClickPeer::SUBNET)) $criteria->add(ClickPeer::SUBNET, $this->subnet);
		if ($this->isColumnModified(ClickPeer::REF)) $criteria->add(ClickPeer::REF, $this->ref);
		if ($this->isColumnModified(ClickPeer::NO_COOKIE)) $criteria->add(ClickPeer::NO_COOKIE, $this->no_cookie);
		if ($this->isColumnModified(ClickPeer::COST)) $criteria->add(ClickPeer::COST, $this->cost);
		if ($this->isColumnModified(ClickPeer::SOCIAL_NETWORK)) $criteria->add(ClickPeer::SOCIAL_NETWORK, $this->social_network);
		if ($this->isColumnModified(ClickPeer::STATUS)) $criteria->add(ClickPeer::STATUS, $this->status);

		return $criteria;
	}

