	public function buildCriteria()
	{
		$criteria = new Criteria(OutgoingFundsPeer::DATABASE_NAME);

		if ($this->isColumnModified(OutgoingFundsPeer::ID)) $criteria->add(OutgoingFundsPeer::ID, $this->id);
		if ($this->isColumnModified(OutgoingFundsPeer::TIME)) $criteria->add(OutgoingFundsPeer::TIME, $this->time);
		if ($this->isColumnModified(OutgoingFundsPeer::APPROVED_AT)) $criteria->add(OutgoingFundsPeer::APPROVED_AT, $this->approved_at);
		if ($this->isColumnModified(OutgoingFundsPeer::ACCOUNT_ID)) $criteria->add(OutgoingFundsPeer::ACCOUNT_ID, $this->account_id);
		if ($this->isColumnModified(OutgoingFundsPeer::USER_ID)) $criteria->add(OutgoingFundsPeer::USER_ID, $this->user_id);
		if ($this->isColumnModified(OutgoingFundsPeer::AMOUNT)) $criteria->add(OutgoingFundsPeer::AMOUNT, $this->amount);
		if ($this->isColumnModified(OutgoingFundsPeer::MONEY)) $criteria->add(OutgoingFundsPeer::MONEY, $this->money);
		if ($this->isColumnModified(OutgoingFundsPeer::STATUS)) $criteria->add(OutgoingFundsPeer::STATUS, $this->status);
		if ($this->isColumnModified(OutgoingFundsPeer::CURRENCY)) $criteria->add(OutgoingFundsPeer::CURRENCY, $this->currency);
		if ($this->isColumnModified(OutgoingFundsPeer::EMONEY_ID)) $criteria->add(OutgoingFundsPeer::EMONEY_ID, $this->emoney_id);
		if ($this->isColumnModified(OutgoingFundsPeer::COMMENT)) $criteria->add(OutgoingFundsPeer::COMMENT, $this->comment);

		return $criteria;
	}

